document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".experience-card").forEach(e=>{const t=e.querySelector(".exp-content");if(!t)return;e.addEventListener("click",()=>{const e=t.classList.contains("is-open");e?(t.style.height=t.scrollHeight+"px",requestAnimationFrame(()=>{t.style.height="0px",t.classList.remove("is-open")})):(t.classList.add("is-open"),t.style.height=t.scrollHeight+"px")}),t.addEventListener("transitionend",e=>{if(e.propertyName!=="height")return;t.classList.contains("is-open")&&(t.style.height="auto")})})})